CREATE OR REPLACE PROCEDURE altera_bloqueio_agenda_ageserv ( ie_status_agenda_p text, ds_motivo_status_p text, nr_seq_motivo_bloq_p bigint, nr_sequencia_lista_p text, ie_opcao_p text, cd_agenda_p bigint, dt_agenda_lista_p text, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


/*	ie_opcao_p
	B - Bloquear
*/
nr_sequencia_lista_w		varchar(2000);
nr_sequencia_w			bigint;
dt_agenda_lista_w			varchar(2000);
dt_agenda_w			varchar(19);
ie_mostrar_motivo_bloq_w		varchar(1);
ds_log_w				varchar(255);
ds_log_texto_base_w		varchar(255);


BEGIN

if (ie_opcao_p IS NOT NULL AND ie_opcao_p::text <> '') and (nr_sequencia_lista_p IS NOT NULL AND nr_sequencia_lista_p::text <> '') then
	begin
	-- Agenda de Serviços - Parâmetro [79] - Solicitar o motivo ao realizar o bloqueio do horário da agenda
	ie_mostrar_motivo_bloq_w := obter_param_usuario(866, 79, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_mostrar_motivo_bloq_w);

	nr_sequencia_lista_w	:= nr_sequencia_lista_p;
	dt_agenda_lista_w		:= dt_agenda_lista_p;
	ds_log_texto_base_w		:= substr(obter_texto_tasy(49892, wheb_usuario_pck.get_nr_seq_idioma),1,255);
	while (nr_sequencia_lista_w IS NOT NULL AND nr_sequencia_lista_w::text <> '') loop
		begin
		nr_sequencia_w		:= substr(nr_sequencia_lista_w, 1, position(',' in nr_sequencia_lista_w) - 1);
		nr_sequencia_lista_w	:= substr(nr_sequencia_lista_w, position(',' in nr_sequencia_lista_w) + 1, length(nr_sequencia_lista_w));

		if (nr_sequencia_w IS NOT NULL AND nr_sequencia_w::text <> '') then
			begin
			
				update	agenda_consulta
				set	ie_status_agenda	= ie_status_agenda_p,
					dt_atualizacao	= clock_timestamp(),
					ds_motivo_status	= ds_motivo_status_p,
					nr_seq_motivo_bloq	= CASE WHEN nr_seq_motivo_bloq_p=0 THEN null  ELSE nr_seq_motivo_bloq_p END ,
					nm_usuario	= nm_usuario_p
				where	nr_sequencia	= nr_sequencia_w;
				end;

			dt_agenda_w	:= substr(dt_agenda_lista_w, 1, position(',' in dt_agenda_lista_w) - 1);
			dt_agenda_lista_w	:= substr(dt_agenda_lista_w, position(',' in dt_agenda_lista_w) + 1, length(dt_agenda_lista_w));

			/*ds_log_w	:= ds_log_texto_base_w;
			ds_log_w	:= replace(ds_log_w, '#@CD_AGENDA_P#@', cd_agenda_p);
			ds_log_w	:= replace(ds_log_w, '#@DT_AGENDA#@', dt_agenda_w);

			insert	into logxxxxxx_tasy
				(cd_log,
				ds_log,
				dt_atualizacao,
				nm_usuario)
			values	(605,
				ds_log_w,
				sysdate,
				nm_usuario_p);*/

			--end;
		end if;
		end;
	end loop;
	end;
end if;

commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
