CREATE OR REPLACE PROCEDURE altera_conta_banc_titulos_lote ( nr_titulo_p bigint, nr_seq_conta_banco_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_bloqueto_w		varchar(44);						
						 

BEGIN 
if (nr_titulo_p IS NOT NULL AND nr_titulo_p::text <> '') then 
	select	a.nr_bloqueto 
	into STRICT	nr_bloqueto_w 
	from	titulo_receber a 
	where	a.nr_titulo = nr_titulo_p;
	 
	update	titulo_receber 
	set	nr_seq_conta_banco 	= nr_seq_conta_banco_p, 
		nm_usuario	  	= nm_usuario_p, 
		dt_atualizacao		= clock_timestamp() 
	where	nr_titulo 		= nr_titulo_p;
		 
	if (nr_bloqueto_w IS NOT NULL AND nr_bloqueto_w::text <> '') then 
		CALL gerar_bloqueto_tit_rec(nr_titulo_p, 'MTR');
	end if;		
	 
	commit;
end if;	
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
