CREATE OR REPLACE PROCEDURE altera_dados_protocolo_onco ( nr_seq_paciente_p text) AS $body$
DECLARE


cd_protocolo_w 		bigint;
nr_seq_medicacao_w 	integer;
nr_dias_intervalo_w	smallint;
nr_ciclos_w		smallint;
ds_observacao_w		varchar(255);
qt_auc_w		real;
qt_tempo_medic_w 	bigint;


BEGIN

select 	max(cd_protocolo),
	max(nr_seq_medicacao)
into STRICT	cd_protocolo_w,
	nr_seq_medicacao_w
from 	paciente_setor
where 	nr_seq_paciente = nr_seq_paciente_p;


if (cd_protocolo_w > 0) and (nr_seq_medicacao_w > 0 ) then

	SELECT  max(nr_dias_intervalo),
		max(nr_ciclos),
		max(qt_auc),
		max(ds_observacao),
		max(qt_tempo_medic)
	into STRICT	nr_dias_intervalo_w,
		nr_ciclos_w,
		qt_auc_w,
		ds_observacao_w,
		qt_tempo_medic_w
	from   	protocolo_medicacao
	where  	cd_protocolo = cd_protocolo_w
	and	nr_sequencia = nr_seq_medicacao_w;

	update	paciente_setor
	set	qt_dias_intervalo = nr_dias_intervalo_w,
		nr_ciclos = nr_ciclos_w,
		ds_observacao = ds_observacao_w,
		qt_tempo_medic = qt_tempo_medic_w
	where	nr_seq_paciente = nr_seq_paciente_p;

	commit;

	if (qt_auc_w <> '') then

		update	paciente_setor
		set	qt_auc = qt_auc_w
		where	nr_seq_paciente = nr_seq_paciente_p;

	commit;

	end if;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
