CREATE OR REPLACE PROCEDURE altera_dt_validad_ordem_quimio ( nr_seq_ordem_lista_p text, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


ie_atualiza_data_validade_p	varchar(1);


BEGIN

/* Quimioterapia - Parâmetro [214] - Gerar a validade da ordem de acordo com a menor validade dos medicamentos que a compõe */

ie_atualiza_data_validade_p := obter_param_usuario(3130, 214, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_atualiza_data_validade_p);

if (ie_atualiza_data_validade_p = 'S') then
	begin
	update	can_ordem_prod
	set	dt_validade_ordem = obter_menor_validade(nr_sequencia)
	where	substr(obter_se_contido(nr_sequencia, nr_seq_ordem_lista_p), 1,1) = 'S'
	and	coalesce(dt_validade_ordem::text, '') = '';
	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
