CREATE OR REPLACE PROCEDURE altera_estab_usuario ( nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


qt_registros_w	bigint;


BEGIN
Select	count(*)
into STRICT	qt_registros_w
from	funcao_param_usuario
where	nm_usuario_param = nm_usuario_p
and	cd_funcao = 0
and	nr_sequencia = 5;

if (qt_registros_w > 0) then
	update	funcao_param_usuario
	set	vl_parametro = cd_estabelecimento_p,
		nm_usuario = nm_usuario_p,
		dt_atualizacao = clock_timestamp()
	where	nm_usuario_param = nm_usuario_p
	and	cd_funcao = 0
	and	nr_sequencia = 5;
else
	insert into funcao_param_usuario(	cd_funcao,
						nr_sequencia,
						nm_usuario_param,
						dt_atualizacao,
						nm_usuario,
						vl_parametro,
						ds_observacao)
				values (		0,
						5,
						nm_usuario_p,
						clock_timestamp(),
						nm_usuario_p,
						cd_estabelecimento_p,
						'');
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
