CREATE OR REPLACE PROCEDURE altera_etapas_hemoterapia ( ie_opcao_p text, nr_prescricao_p bigint, nr_seq_procedimento_p bigint, nr_sequencia_p bigint) AS $body$
BEGIN

if (ie_opcao_p IS NOT NULL AND ie_opcao_p::text <> '') then

	if (ie_opcao_p = '1') then
		update prescr_proc_hor
		set	   dt_interrupcao = clock_timestamp(),
			   dt_fim_horario  = NULL
		where  nr_prescricao = nr_prescricao_p
		and    nr_seq_procedimento = nr_seq_procedimento_p
		and	   nr_sequencia = nr_sequencia_p;
	end if;

	commit;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
