CREATE OR REPLACE PROCEDURE altera_login_alt_usuario ( ds_login_alternativo_p text, nm_usuario_param_p text, nm_usuario_p text) AS $body$
BEGIN

if (ds_login_alternativo_p IS NOT NULL AND ds_login_alternativo_p::text <> '') and (nm_usuario_param_p IS NOT NULL AND nm_usuario_param_p::text <> '') then
	update	usuario
	set	ds_login		= ds_login_alternativo_p,
		nm_usuario_atual	= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nm_usuario		= nm_usuario_param_p;
end if;

commit;

end;
