CREATE OR REPLACE PROCEDURE altera_medico_resp_escala ( cd_estabelecimento_p bigint, ie_clinica_p bigint, nr_atendimento_p bigint) AS $body$
DECLARE

 
cd_medico_resp_w	varchar(10);	
dt_referencia_w		timestamp;
ie_tipo_atendimento_w	smallint;

BEGIN
 
select	max(dt_entrada), 
	max(ie_tipo_atendimento) 
into STRICT	dt_referencia_w, 
	ie_tipo_atendimento_w 
from	atendimento_paciente 
where	nr_atendimento = nr_atendimento_p;
 
select	obter_pessoa_escala_atend(0,cd_estabelecimento_p,ie_tipo_atendimento_w,ie_clinica_p,dt_referencia_w) 
into STRICT	cd_medico_resp_w
;
 
if (coalesce(cd_medico_resp_w,0) > 0) then 
 
	update	atendimento_paciente 
	set	cd_medico_resp = cd_medico_resp_w 
	where	nr_atendimento = nr_atendimento_p;
 
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
