CREATE OR REPLACE PROCEDURE altera_ordem_imagem_captura ( nr_ordem_seq_p bigint, nr_sequencia_p bigint) AS $body$
DECLARE


nr_ordem_old_w     bigint;

nr_seq_log_atual_w bigint;
ie_log_w           varchar(10);


BEGIN
  SELECT nr_ordem_apresentacao
  INTO STRICT   nr_ordem_old_w
  FROM   captura_imagem
  WHERE  nr_sequencia = nr_sequencia_p;

  SELECT * FROM gravar_log_alteracao(nr_ordem_old_w, nr_ordem_seq_p, obter_usuario_ativo, nr_seq_log_atual_w, 'NR_ORDEM_APRESENTACAO', ie_log_w, NULL, 'CAPTURA_IMAGEM', nr_sequencia_p, NULL) INTO STRICT nr_seq_log_atual_w, ie_log_w;

  UPDATE captura_imagem
  SET    nr_ordem_apresentacao = nr_ordem_seq_p
  WHERE  nr_sequencia = nr_sequencia_p;

  COMMIT;

END;
