CREATE OR REPLACE PROCEDURE altera_prof_checkup_conta ( NR_SEQ_CHECKUP_ETAPA_p bigint, cd_pessoa_fisica_p text, nm_usuario_p text) AS $body$
DECLARE

 
					 
 
 
ie_medico_w		varchar(10);
nr_sequencia_w		bigint;
nr_interno_conta_w	bigint;
ie_status_acerto_w	varchar(10);
					
						 
C01 CURSOR FOR 
	SELECT	a.nr_sequencia, 
		a.nr_interno_conta, 
		b.ie_status_acerto 
	from	procedimento_paciente a, 
		conta_paciente b 
	where	a.nr_interno_conta	= b.nr_interno_conta 
	and	a.NR_SEQ_CHECKUP_ETAPA	= NR_SEQ_CHECKUP_ETAPA_p;
						

BEGIN 
 
ie_medico_w	:= obter_se_medico(cd_pessoa_fisica_p,'M');
 
 
open C01;
loop 
fetch C01 into	 
	nr_sequencia_w, 
	nr_interno_conta_w, 
	ie_status_acerto_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	 
	if (ie_status_acerto_w	= 2) then 
		CALL Wheb_mensagem_pck.exibir_mensagem_abort(277383);
	end if;
	 
	 
	if (ie_medico_w	= 'S') then 
		update	procedimento_paciente 
		set	CD_MEDICO_EXECUTOR	= cd_pessoa_fisica_p 
		where	nr_sequencia		= nr_sequencia_w;
	else 
		update	procedimento_paciente 
		set	CD_PESSOA_FISICA	= cd_pessoa_fisica_p, 
			CD_MEDICO_EXECUTOR  = NULL 
		where	nr_sequencia		= nr_sequencia_w;
	end if;
	 
	end;
end loop;
close C01;	
 
 
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
