CREATE OR REPLACE PROCEDURE altera_qtd_entrega_oci ( nr_ordem_compra_p bigint, nr_item_oci_p bigint, qt_entrega_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_entrega_w		bigint;


BEGIN

select count(*)
into STRICT qt_entrega_w
from ordem_compra_item_entrega
where nr_ordem_compra = nr_ordem_compra_p
and nr_item_oci = nr_item_oci_p;

if (qt_entrega_w = 1)then
begin

	update ordem_compra_item_entrega
	set qt_prevista_entrega = coalesce(qt_entrega_p,0)
	where nr_ordem_compra = nr_ordem_compra_p
	and nr_item_oci = nr_item_oci_p  LIMIT 1;

	commit;
end;
end if;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
