SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE altera_resp_contratos (nr_sequencia_p bigint, cd_pessoa_resp_p text, nm_usuario_p text, cd_cargo_p bigint, cd_setor_p text) AS $body$
DECLARE

 
cd_pessoa_resp_velha_w		varchar(10);
ds_pessoa_resp_velha_w		varchar(100);
ds_pessoa_resp_novo_w		varchar(100);
ds_historico_w			varchar(4000);
cd_cargo_w			bigint;
cd_setor_w			integer;


BEGIN 
 
select	cd_pessoa_resp, 
	substr(coalesce(obter_nome_pf(cd_pessoa_resp),wheb_mensagem_pck.get_texto(244229)),1,100) ds_pessoa_resp, 
	substr(obter_nome_pf(cd_pessoa_resp_p),1,100) ds_pessoa_resp_novo, 
	CASE WHEN coalesce(cd_cargo_p,0)=0 THEN  cd_cargo  ELSE cd_cargo_p END , 
	CASE WHEN coalesce(cd_setor_p,0)=0 THEN  cd_setor  ELSE cd_setor_p END  
into STRICT	cd_pessoa_resp_velha_w, 
	ds_pessoa_resp_velha_w, 
	ds_pessoa_resp_novo_w, 
	cd_cargo_w, 
	cd_setor_w 
from	contrato 
where	nr_sequencia = nr_sequencia_p;
 
update	contrato 
set	cd_pessoa_resp = cd_pessoa_resp_p, 
	cd_cargo = cd_cargo_w, 
	cd_setor = cd_setor_w 
where	nr_sequencia = nr_sequencia_p;
 
ds_historico_w := substr(wheb_mensagem_pck.get_texto(315111,	'CD_PESSOA_RESP_VELHA_W='||CD_PESSOA_RESP_VELHA_W||';DS_PESSOA_RESP_VELHA_W='||DS_PESSOA_RESP_VELHA_W|| 
								';CD_PESSOA_RESP_P='||CD_PESSOA_RESP_P||';DS_PESSOA_RESP_NOVO_W='||DS_PESSOA_RESP_NOVO_W),1,2000);
insert into contrato_historico( 
		nr_sequencia, 
		nr_seq_contrato, 
		dt_historico, 
		ds_historico, 
		dt_atualizacao, 
		nm_usuario, 
		ds_titulo, 
		dt_liberacao, 
		nm_usuario_lib, 
		ie_efetivado) 
values (	nextval('contrato_historico_seq'), 
		nr_sequencia_p, 
		clock_timestamp(), 
		ds_historico_w, 
		clock_timestamp(), 
		nm_usuario_p, 
		wheb_mensagem_pck.get_texto(315113), 
		clock_timestamp(), 
		nm_usuario_p, 
		'N');
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
