CREATE OR REPLACE PROCEDURE altera_senha_banco ( ds_senha_p text, nm_user_p text) AS $body$
BEGIN

CALL Exec_Sql_Dinamico('Tasy', 'alter user ' || nm_user_p || ' identified by "' ||ds_senha_p || '"');

update	tasy_versao.tasy_seg
set	ds_senha	= upper(RAWTOHEX(wheb_seguranca.encrypt(ds_senha_p)))
where	upper(nm_user)	= upper(nm_user_p);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
