CREATE OR REPLACE PROCEDURE altera_setor_usuario ( cd_pf_p bigint) AS $body$
DECLARE


cd_setor_w    bigint;


BEGIN

	select	max(cd_setor_atendimento)
	into STRICT	cd_setor_w
	from	pessoa_fisica_loc_trab
	where	cd_pessoa_fisica = cd_pf_p
	and	ie_local_principal = 'S';

	update	usuario
	set	cd_setor_atendimento	= cd_setor_w
	where	cd_pessoa_fisica		= cd_pf_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
