SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE altera_situacao_contrato ( nr_sequencia_p bigint, ie_situacao_p text, nm_usuario_p text) AS $body$
DECLARE


situacao_w			varchar(10);
ds_historico_w			varchar(4000);
nr_seq_hist_w			bigint;
ie_sit_aux_w			varchar(10);
nr_seq_hist_aux_w			bigint;
count_w				bigint;




BEGIN

	select  count(*)
	into STRICT	count_w
	from 	contrato_historico a
	where 	a.nr_seq_contrato = nr_sequencia_p
	AND	a.ds_titulo = wheb_mensagem_pck.get_texto(795659)--'Alteração na situação do contrato'
	and 	a.nr_sequencia = (SELECT MAX(b.nr_sequencia) FROM contrato_historico b);

   if count_w > 0 then

	SELECT coalesce(SUBSTR(a.ds_historico,44,1),'Q'), coalesce(a.nr_sequencia,0)
	into STRICT	ie_sit_aux_w,
		nr_seq_hist_aux_w
	FROM   contrato_historico a
	WHERE  a.nr_sequencia = (SELECT MAX(b.nr_sequencia) FROM contrato_historico b)
	AND        a.nr_seq_contrato = nr_sequencia_p
	AND	  a.ds_titulo = wheb_mensagem_pck.get_texto(795659)--'Alteração na situação do contrato'
	ORDER BY 2 DESC LIMIT 1;
   end if;



if ((ie_sit_aux_w <> ie_situacao_p) or (count_w = 0)) then

	select CASE WHEN ie_situacao_p='A' THEN  wheb_mensagem_pck.get_texto(249424)  ELSE wheb_mensagem_pck.get_texto(306063) END  into STRICT situacao_w;

	update	contrato
	set	ie_situacao	= ie_situacao_p
	where  ((nr_seq_contrato_atual = nr_sequencia_p) --or (nr_sequencia = nr_sequencia_p)
	or (cd_contrato = to_char(nr_sequencia_p)));

			ds_historico_w	:= substr(	wheb_mensagem_pck.get_texto(315114) || chr(13) || chr(10) ||
							wheb_mensagem_pck.get_texto(280393) || situacao_w ,1,4000);


			select nextval('contrato_historico_seq')
			into STRICT	nr_seq_hist_w
			;



			insert into contrato_historico(
				nr_sequencia,
				nr_seq_contrato,
				dt_historico,
				ds_historico,
				dt_atualizacao,
				nm_usuario,
				ds_titulo,
				dt_liberacao,
				nm_usuario_lib,
				ie_efetivado)
				values (	nr_seq_hist_w,
				nr_sequencia_p,
				clock_timestamp(),
				ds_historico_w,
				clock_timestamp(),
				nm_usuario_p,
				wheb_mensagem_pck.get_texto(315115),
				clock_timestamp(),
				nm_usuario_p,
				'N');

	commit;
end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
