CREATE OR REPLACE PROCEDURE altera_status_com_prev_implant ( nr_sequencia_p bigint, ie_opcao_p text, nm_usuario_p text) AS $body$
DECLARE


ie_permite_opcao_p	varchar(1);

BEGIN
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (ie_opcao_p IS NOT NULL AND ie_opcao_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin

	if (ie_opcao_p = 'L') then
		begin
		select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
		into STRICT	ie_permite_opcao_p
		from	com_prev_implant
		where	coalesce(dt_cancelamento::text, '') = ''
		and	coalesce(dt_liberacao::text, '') = ''
		and	nr_sequencia 	= nr_sequencia_p;

		if (ie_permite_opcao_p = 'S') then
			begin
			update	com_prev_implant
			set	dt_liberacao		= clock_timestamp(),
				nm_usuario_liberacao	= nm_usuario_p,
				nm_usuario		= nm_usuario_p
			where	nr_sequencia 		= nr_sequencia_p;
			end;
		end if;
		end;
	elsif (ie_opcao_p = 'C') then
		begin
		select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
		into STRICT	ie_permite_opcao_p
		from	com_prev_implant
		where	coalesce(dt_cancelamento::text, '') = ''
		and	nr_sequencia 	= nr_sequencia_p;

		if (ie_permite_opcao_p = 'S') then
			begin
			update	com_prev_implant
			set	dt_cancelamento		= clock_timestamp(),
				nm_usuario_cancelamento	= nm_usuario_p,
				nm_usuario		= nm_usuario_p
			where	nr_sequencia 		= nr_sequencia_p;
			end;
		end if;
		end;
	end if;
	end;
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
