CREATE OR REPLACE PROCEDURE altera_status_conj_cme ( cd_status_conj_p bigint, nr_sequencia_p bigint, ie_expurgo_p text, nm_usuario_p text, nm_usuario_orig_p text default null) AS $body$
DECLARE


nr_seq_embalagem_w		bigint;
dt_validade_w			timestamp;


BEGIN

if (coalesce(cd_status_conj_p,0) > 0) and (coalesce(nr_sequencia_p,0) > 0) and (coalesce(ie_expurgo_p,'X') <> 'X') then
	
	if (cd_status_conj_p = 1) then
		
		select	nr_seq_embalagem
		into STRICT	nr_seq_embalagem_w
		from	cm_conjunto_cont
		where	nr_sequencia = nr_sequencia_p;
		
		select (clock_timestamp() + qt_dia_validade)
		into STRICT	dt_validade_w
		from	cm_classif_embalagem
		where	nr_sequencia = nr_seq_embalagem_w;
		
	end if;
	
	update	cm_conjunto_cont
	set	ie_status_conjunto = cd_status_conj_p,
		ie_expurgo = ie_expurgo_p,
		nm_usuario = nm_usuario_p,
		dt_atualizacao = clock_timestamp(),
		nm_usuario_origem = coalesce(nm_usuario_orig_p,nm_usuario_origem),
		dt_validade = CASE WHEN dt_validade_w = NULL THEN null  ELSE dt_validade_w END
	where	nr_sequencia = nr_sequencia_p;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
