SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE altera_status_conj_vencido () AS $body$
DECLARE


nr_sequencia_w		bigint;

/* Busca conjuntos com validade vencida, que nao foram atendidos */

c01 CURSOR FOR

	SELECT	nr_sequencia
	from	cm_conjunto_cont
	where	coalesce(ie_situacao,'A') = 'A'
	and	ie_status_conjunto = 3
	and	dt_validade < clock_timestamp()
	and cm_obter_dados_conjunto(nr_seq_conjunto,'S') = 'A'
	order by nr_sequencia;


BEGIN

open c01;
loop
fetch c01 into	
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	/* ie_status_conjunto = 10: Vencido */

	update	cm_conjunto_cont
	set	ie_status_conjunto = 10,
		dt_atualizacao = clock_timestamp(),
		nm_usuario = 'Tasy'
	where	nr_sequencia = nr_sequencia_w;
	
	/* Gera novo conjunto_cont com status Aguardando esterilizacao */

	CALL cm_gerar_conj_retorno_atend(nr_sequencia_w,'Tasy');
	end;
end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE altera_status_conj_vencido () FROM PUBLIC;

