CREATE OR REPLACE PROCEDURE altera_status_gv_aguardando ( cd_pessoa_fisica_p text, nm_usuario_p text) AS $body$
DECLARE

 
ie_existe_gv_w	varchar(1);
nr_sequencia_w	bigint;			
			 

BEGIN 
 
select	coalesce(max(nr_sequencia),0) 
into STRICT	nr_sequencia_w 
from	gestao_vaga 
where	cd_pessoa_fisica = cd_pessoa_fisica_p 
and	ie_status = 'A';
 
if (nr_sequencia_w > 0) then 
 
	update	gestao_vaga 
	set	ie_status 	= 'R', 
		dt_atualizacao 	= clock_timestamp(), 
		nm_usuario 	= nm_usuario_p 
	where	nr_sequencia	= nr_sequencia_w;
 
end if;
 
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
