CREATE OR REPLACE PROCEDURE altera_status_item_inventario (nr_sequencia_p bigint, nr_seq_inventario_p bigint, ie_status_p text, /* Informar o novo status que o item irá receber */
 ie_processo_p text) AS $body$
DECLARE


qt_existe_w		bigint;
cd_estabelecimento_w	integer;
ie_contagem_atual_w	varchar(15);

/* ie_processo_p
A - Alteração da coluna de contagem
R - Recontagem do item
B - Bloqueio do item
D - Desbloqueio do item

   ie_status_p
P - Pendente
E - Em conferência
C - Colcluído
D - Definido
F - Finallizado
*/
BEGIN

/* Aqui faz update de um item especifico */

if (nr_sequencia_p > 0) then
	begin
	select	count(*)
	into STRICT	qt_existe_w
	from	inventario_material
	where	nr_seq_inventario 	= nr_seq_inventario_p
	and	nr_sequencia 	= nr_sequencia_p;

	if (qt_existe_w > 0) then
		begin

		if (ie_status_p in ('C','E','D')) then
			update	inventario_material
			set	ie_status_inventario = ie_status_p
			where	nr_seq_inventario 	= nr_seq_inventario_p
			and	nr_sequencia 	= nr_sequencia_p;
		elsif (ie_status_p = 'P') then
			begin
			if (ie_processo_p in ('R','B','D')) then
				begin
				select	coalesce(max(ie_contagem_atual), '1')
				into STRICT	ie_contagem_atual_w
				from	inventario_material
				where	nr_seq_inventario 	= nr_seq_inventario_p
				and	nr_sequencia      	= nr_sequencia_p;

				if (ie_contagem_atual_w = '1') then
					update	inventario_material
					set	qt_contagem	 = NULL,
						ie_status_inventario	= ie_status_p
					where	nr_sequencia	= nr_sequencia_p;
				elsif (ie_contagem_atual_w = '2') then
					update	inventario_material
					set	qt_recontagem	 = NULL,
						ie_status_inventario	= ie_status_p
					where	nr_sequencia	= nr_sequencia_p;
				elsif (ie_contagem_atual_w = '3') then
					update	inventario_material
					set	qt_seg_recontagem	 = NULL,
						ie_status_inventario	= ie_status_p
					where	nr_sequencia	= nr_sequencia_p;
				elsif (ie_contagem_atual_w = '4') then
					update	inventario_material
					set	qt_terc_recontagem	 = NULL,
						ie_status_inventario	= ie_status_p
					where	nr_sequencia	= nr_sequencia_p;
				end if;
				end;
			elsif (ie_processo_p = 'A') then
				update	inventario_material
				set	ie_status_inventario = ie_status_p
				where	nr_sequencia	= nr_sequencia_p;
			end if;

			/* Verifica se o item foi bloqueado */

			select	count(*)
			into STRICT	qt_existe_w
			from	inventario_bloqueio
			where	nr_seq_inventario	= nr_seq_inventario_p
			and	nr_seq_item 	= nr_sequencia_p;

			if (qt_existe_w > 0) then
				delete	FROM inventario_bloqueio
				where	nr_seq_inventario	= nr_seq_inventario_p
				and	nr_seq_item 	= nr_sequencia_p;
			end if;

			end;
		end if;
		end;
	end if;
	end;
else	/* Aqui faz update de todos os itens do inventario */
	begin

	select	count(*)
	into STRICT	qt_existe_w
	from	inventario_material
	where	nr_seq_inventario = nr_seq_inventario_p;

	if (qt_existe_w > 0) then
		update	inventario_material
		set	ie_status_inventario	= ie_status_p
		where	nr_seq_inventario 	= nr_seq_inventario_p;
	end if;

	/* Verifica se o item foi bloqueado */

	select	count(*)
	into STRICT	qt_existe_w
	from	inventario_bloqueio
	where	nr_seq_inventario = nr_seq_inventario_p;

	if (qt_existe_w > 0) then
		delete	FROM inventario_bloqueio
		where	nr_seq_inventario = nr_seq_inventario_p;
	end if;

	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
