SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE alternar_situacao_usuario (nm_usuario_p text) AS $body$
BEGIN

if (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin
	
	update	usuario
	set	ie_situacao		= CASE WHEN coalesce(ie_situacao, 'X')='A' THEN  'I'  ELSE 'A' END
	where	nm_usuario = nm_usuario_p;	
	
	update	usuario
	set	dt_inativacao		= CASE WHEN coalesce(ie_situacao, 'X')='I' THEN  clock_timestamp()  ELSE null END
	where	nm_usuario = nm_usuario_p;
	commit;
	
	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
