CREATE OR REPLACE PROCEDURE alterna_conferencia_conta ( nm_tabela_p text, nr_sequencia_p bigint, nm_usuario_p text) AS $body$
BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	begin
	CALL EXEC_SQL_DINAMICO(nm_usuario_p,' update ' ||nm_tabela_p||
	' set dt_conferencia = decode(dt_conferencia, null, sysdate, null), dt_atualizacao = sysdate, nm_usuario = ' || chr(39) ||nm_usuario_p|| chr(39) ||
	' where nr_sequencia = ' || chr(39) ||nr_sequencia_p|| chr(39));
	commit;
	end;
end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
