CREATE OR REPLACE PROCEDURE alter_pessoa_fisica_aux ( cd_pessoa_fisica_p text, ie_receive_sms_p text, nm_usuario_p text) AS $body$
DECLARE

					
ie_possui_aux_w 	varchar(1);
					

BEGIN				

select CASE WHEN count(*)=0 THEN  'N'  ELSE 'S' END
into STRICT ie_possui_aux_w
from pessoa_fisica_aux
where cd_pessoa_fisica = cd_pessoa_fisica_p;

if (ie_possui_aux_w = 'S') then
	
	update 	pessoa_fisica_aux
	set 	ie_receive_sms = ie_receive_sms_p,
		nm_usuario_nrec = nm_usuario_p,
		dt_atualizacao_nrec = clock_timestamp()
	where	cd_pessoa_fisica = cd_pessoa_fisica_p;
else

	insert into pessoa_fisica_aux(	
		nr_sequencia,
		cd_pessoa_fisica,
		dt_atualizacao, 
		dt_atualizacao_nrec,
		nm_usuario,
		nm_usuario_nrec,
		ie_receive_sms)
	values (
		nextval('pessoa_fisica_aux_seq'),
		cd_pessoa_fisica_p, 	
		clock_timestamp(),
		clock_timestamp(),
		nm_usuario_p,
		nm_usuario_p,
		ie_receive_sms_p);
end if;
commit;				
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
