CREATE OR REPLACE PROCEDURE alter_status_lista_reab ( vl_novo_p text, nr_seq_lista_reab_p bigint, nr_seq_agenda_p bigint, ie_status_p text, ie_agenda_dia_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_lista_reab_w	bigint;
dt_agenda_w		timestamp;
cd_pessoa_fisica_w	varchar(10);

C01 CURSOR FOR
	SELECT	nr_seq_lista_reab
	from	agenda_consulta
	where	trunc(dt_agenda,'dd') = trunc(dt_agenda_w)
	and	ie_status_agenda = 'F'
	and	cd_pessoa_fisica = cd_pessoa_fisica_w;


BEGIN

if (ie_status_p = 'F') then
	select	max(dt_agenda),
		max(cd_pessoa_fisica)
	into STRICT	dt_agenda_w,
		cd_pessoa_fisica_w
	from	agenda_consulta
	where	nr_sequencia = nr_seq_agenda_p;

	if (ie_agenda_dia_p = 'S') then
		open C01;
		loop
		fetch C01 into
			nr_seq_lista_reab_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
			begin
			update	rp_lista_espera_modelo
			set	ie_status	= upper(vl_novo_p),
				nm_usuario	= nm_usuario_p,
				dt_atualizacao	= clock_timestamp()
			where	nr_sequencia	= nr_seq_lista_reab_w;
			end;
		end loop;
		close C01;
	end if;
end if;

if (coalesce(nr_seq_lista_reab_p,0) > 0) then

	update	rp_lista_espera_modelo
	set	ie_status	= upper(vl_novo_p),
		nm_usuario	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp()
	where	nr_sequencia	= nr_seq_lista_reab_p;
end if;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
