CREATE OR REPLACE PROCEDURE alt_dt_contabil_adiant_html5 ( dt_contabil_p timestamp, nm_usuario_p text, nr_adiantamento_p bigint) AS $body$
DECLARE


dt_contabil_ant_w	adiantamento.dt_contabil%type;


BEGIN

select	max(dt_contabil)
into STRICT	dt_contabil_ant_w
from	adiantamento
where	nr_adiantamento	= nr_adiantamento_p;

update	adiantamento
set	dt_contabil = dt_contabil_p,
	dt_atualizacao = clock_timestamp(),
	nm_usuario = nm_usuario_p
where nr_adiantamento = nr_adiantamento_p;

CALL GERAR_ADIANTAMENTO_HIST(nr_adiantamento_p,
	'A',
	clock_timestamp(),
	wheb_mensagem_pck.GET_TEXTO(210394, 'DATA_ANT=' || dt_contabil_ant_w || ';DATA_ATUAL=' || dt_contabil_p),
	nm_usuario_p,
	'N');

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
