CREATE OR REPLACE PROCEDURE alt_status_atend_prescr_proc ( nr_prescricao_p bigint, nr_seq_prescr_proc_p bigint, ie_acao_p text, nm_usuario_p text, ie_status_atend_p text, ie_coleta_rla_pda_p text) AS $body$
DECLARE

/* IE_ACAO_P 
* Verifica se está confirmando a coleta ou se está coletando os exames em contigência. 
* CONFIRM - Confirmando coleta 
* SEMETIQUETA - Coletando exames em contingência 
*/
 
 
nr_conta_paciente_w			procedimento_paciente.nr_interno_conta%type;
nr_seq_procpaci_w			procedimento_paciente.nr_sequencia%type;
nr_atendimento_w			atendimento_paciente.nr_atendimento%type;


BEGIN 
if (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') and (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (ie_status_atend_p IS NOT NULL AND ie_status_atend_p::text <> '') and (nr_seq_prescr_proc_p IS NOT NULL AND nr_seq_prescr_proc_p::text <> '') then 
	begin 
	update	prescr_procedimento 
	set	ie_status_atend	= ie_status_atend_p, 
		nm_usuario = nm_usuario_p, 
		dt_atualizacao = clock_timestamp(), 
		ie_coleta_rla_pda = ie_coleta_rla_pda_p 
	where	nr_sequencia	= nr_seq_prescr_proc_p 
	and	nr_prescricao	= nr_prescricao_p 
	and	((ie_status_atend = 12 
	and	ie_acao_p = 'CONFIRM') 
	or	ie_acao_p = 'SEMETIQUETA');
	 
	 
	update 	prescr_proc_controle_etiq 
	set	ie_status_atend = ie_status_atend_p, 
		nm_usuario = nm_usuario_p, 
		dt_atualizacao = clock_timestamp() 
	where	nr_prescricao 	= nr_prescricao_p 
	and	ie_status_atend = 12 
	and	((ie_status_atend = 12 
	and	ie_acao_p = 'CONFIRM') 
	or	ie_acao_p = 'SEMETIQUETA');
	 
	 
	if (ie_coleta_rla_pda_p = 'S') then 
		select max(nr_interno_conta), 
				max(nr_sequencia), 
				max(nr_atendimento) 
		into STRICT	nr_conta_paciente_w, 
			  nr_seq_procpaci_w, 
				nr_atendimento_w 
		from 	procedimento_paciente 
		where	nr_prescricao = nr_prescricao_p 
		and		nr_sequencia_prescricao = nr_seq_prescr_proc_p;
		 
		if (nr_conta_paciente_w IS NOT NULL AND nr_conta_paciente_w::text <> '') then 
			CALL gerar_lancamento_automatico(nr_atendimento_w,null,557,nm_usuario_p,nr_seq_procpaci_w,null,null,null,null,nr_conta_paciente_w);
		end if;
	end if;
	end;
end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
