CREATE OR REPLACE PROCEDURE alt_status_envio_cot_compra ( nr_cot_compra_p cot_compra.nr_cot_compra%type, ie_status_envio_p cot_compra.ie_status_envio%type) AS $body$
DECLARE


qt_cot_compra_w		bigint;
ie_status_envio_w	cot_compra.ie_status_envio%type;


BEGIN

select	count(1)
into STRICT	qt_cot_compra_w
from	cot_compra
where	nr_cot_compra = nr_cot_compra_p;

if (qt_cot_compra_w > 0) then
	begin

	if (ie_status_envio_p = '1') then
		ie_status_envio_w := 'O';
	elsif (ie_status_envio_p = '0') then
		ie_status_envio_w := 'E';
	elsif (ie_status_envio_p = '-1') then
		ie_status_envio_w := 'E';
	else
		ie_status_envio_w := ie_status_envio_p;
	end if;

	update	cot_compra
	set	ie_status_envio = ie_status_envio_w
	where	nr_cot_compra = nr_cot_compra_p;

	commit;

	end;
end if;



end;
