CREATE OR REPLACE PROCEDURE anexar_exame_pdf ( ds_caminho_p text, nr_prescricao_p bigint, nr_seq_prescr_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_result_lab_w		bigint;


BEGIN

select	coalesce(max(nr_sequencia),0)
into STRICT	nr_seq_result_lab_w
from	result_laboratorio
where	nr_prescricao = nr_prescricao_p
and	nr_seq_prescricao = nr_seq_prescr_p;

if (nr_seq_result_lab_w > 0) then
	update  result_laboratorio
	set     ds_resultado =  ds_caminho_p,
		dt_atualizacao = clock_timestamp(),
		nm_usuario = nm_usuario_p
	where   nr_sequencia =  nr_seq_result_lab_w
	and	ie_formato_texto = 3;
else
	insert into result_laboratorio(
		nr_sequencia,
		ds_resultado,
		nr_prescricao,
		nr_seq_prescricao,
		nm_usuario,
		dt_atualizacao,
		ie_formato_texto,
		ie_cobranca)
	values (nextval('result_laboratorio_seq'),
		ds_caminho_p,
		nr_prescricao_p,
		nr_seq_prescr_p,
		nm_usuario_p,
		clock_timestamp(),
		3,
		'N');
end if;

commit;

end;
