CREATE OR REPLACE PROCEDURE apagar_formular_ficha_financ (nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_formula_w	bigint;


BEGIN

select 	count(*)
into STRICT	qt_formula_w
from 	ficha_financ_formula
where 	nr_sequencia = nr_sequencia_p;

if (qt_formula_w = 1) then
	begin

	update 	ficha_financ_formula
	set 	ds_formula = '',
		nm_usuario = nm_usuario_p,
		dt_atualizacao = clock_timestamp()
	where	nr_sequencia = nr_sequencia_p;

	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
