CREATE OR REPLACE PROCEDURE apagar_reg_template_usuario ( nm_usuario_p text) AS $body$
BEGIN

if (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin

	delete FROM ehr_registro a
	where  a.nm_usuario = nm_usuario_p
	and    coalesce(a.dt_liberacao::text, '') = ''
	and	   not exists (	SELECT 1
						from 	  ehr_reg_template b
						where   b.nr_seq_reg = a.nr_sequencia);

	commit;
	end;
end if;

end;
