SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE apap_atualiza_ds_result_desc ( nr_seq_valor_p w_apap_pac_registro.nr_sequencia%type, ds_valor_p w_apap_pac_registro.ds_result_desc%type) AS $body$
DECLARE

												
ds_unidade_medida_w	w_apap_pac_informacao.ds_unid_med%type;
nr_sequencia_w		w_apap_pac_informacao.nr_sequencia%type;
eh_pai_w			valor_dominio.vl_dominio%type;
									

BEGIN
ds_unidade_medida_w := null;

if (coalesce(nr_seq_valor_p,0) > 0) then
	select	max(info.ds_unid_med) ds_unid_med,
			max(info.nr_sequencia)
	into STRICT	ds_unidade_medida_w,
			nr_sequencia_w
	from	w_apap_pac_informacao info
	where	info.nr_sequencia = (	SELECT	max(reg.nr_seq_apap_inf) nr_seq_apap_inf
									from	w_apap_pac_registro reg
									where	reg.nr_sequencia = nr_seq_valor_p);
									
	select	coalesce(max('S'),'N')
	into STRICT	eh_pai_w
	from	w_apap_pac_informacao
	where	nr_seq_superior = nr_sequencia_w;									

	if (eh_pai_w = 'N') then
		if (ds_unidade_medida_w IS NOT NULL AND ds_unidade_medida_w::text <> '') then
			update	w_apap_pac_registro
			set		ds_result_desc = substr(ds_valor_p || ' ' ||ds_unidade_medida_w,1,255)
			where	nr_sequencia   = nr_seq_valor_p;
		else
			update	w_apap_pac_registro
			set		ds_result_desc = substr(ds_valor_p,1,255)
			where	nr_sequencia   = nr_seq_valor_p;
		end if;	
	end if;
end if;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
