SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE aplicar_ajuste_versao_cloud (cd_versao_p text, nr_release_p bigint, nm_usuario_p text, ds_retorno_p INOUT text, nr_pacote_p bigint, ie_tipo_script_p text) AS $body$
DECLARE


ds_consulta_w   varchar(4000);
ds_update_w     varchar(4000);
ds_where_w      varchar(2000);
ds_erro_w		varchar(2000):='';
nr_seq_ajuste_versao_w	numeric(38);
nr_release_w		    bigint;
nr_service_pack_w		bigint;
nr_pacote_w		        bigint;
qt_objetos_w		bigint;
qt_erros_w		bigint:=0;
nm_objeto_w     varchar(50);
ie_plataform_app_w aplicacao_tasy.ie_tipo_aplicacao%TYPE;
c02              	integer;
retorno_w           integer;
dt_ini_compile timestamp;
dt_fin_compile timestamp;
nm_user_compile timestamp;

nm_fase_w	cloud_upgrade_log.ds_mensagem%type := 'APLICAR_AJUSTE_VERSAO_CLOUD';

c01 CURSOR FOR
	SELECT	 distinct a.nr_release,
			 a.nm_objeto
	from	 ajuste_versao_cliente a
	where	 a.cd_versao = cd_versao_p
	and		 ((ie_tipo_script_p = 'N' AND a.nr_pacote <= nr_pacote_p) or (a.nr_pacote = nr_pacote_p))
	and (a.ie_tipo_script <> 'E')
	and	     a.ie_compila = 'N'
	and      a.nr_ordem not in (66,190) --Evitar duplicidade dic_dados Update Delete Modify
	order by a.nr_release;


BEGIN

    select a.ie_tipo_aplicacao
      into STRICT ie_plataform_app_w 
      from aplicacao_tasy a 
     where lower(a.cd_aplicacao_tasy) = 'tasy';

    ds_consulta_w := ' select distinct a.nr_release, a.nm_objeto, a.nr_service_pack, a.nr_pacote from ajuste_versao_cliente a';
    if (ie_plataform_app_w != 'H') then
         ds_where_w := ' where	 a.cd_versao = '||chr(39)||cd_versao_p||chr(39)|| 
                ' and		 ((('||chr(39)||ie_tipo_script_p||chr(39)||' = ''N'') and (a.nr_pacote <= '|| nr_pacote_p || ')) or (a.nr_pacote = '|| nr_pacote_p ||'))' ||
                ' and 	 (a.ie_tipo_script <> ''E'')' ||
                ' and	     a.ie_compila = ''N'' ' ||
                ' and      a.nr_ordem not in (66,190)';
    else
        ds_where_w := ' where	 a.cd_versao = '||chr(39)||cd_versao_p||chr(39)|| 
                ' and		 a.nr_service_pack <= '|| nr_pacote_p || 
                ' and 	 a.ie_tipo_script <> ''E'' ' ||
                ' and	     a.ie_compila = ''N'' ' ||
                ' and      a.nr_ordem not in (66,190)';
    end if;
    ds_consulta_w := ds_consulta_w || ds_where_w ||' order by a.nr_release';

C02 := DBMS_SQL.OPEN_CURSOR;
DBMS_SQL.PARSE(C02, ds_consulta_w, dbms_sql.Native);
DBMS_SQL.DEFINE_COLUMN(C02,1,nr_release_w);
DBMS_SQL.DEFINE_COLUMN(C02,2,nm_objeto_w,50);
DBMS_SQL.DEFINE_COLUMN(C02,3,nr_service_pack_w);
DBMS_SQL.DEFINE_COLUMN(C02,4,nr_pacote_w);
retorno_w := DBMS_SQL.execute(C02);

while( DBMS_SQL.FETCH_ROWS(C02) > 0 ) loop
    DBMS_SQL.COLUMN_VALUE(C02, 1, nr_release_w);
    DBMS_SQL.COLUMN_VALUE(C02, 2, nm_objeto_w);
    DBMS_SQL.COLUMN_VALUE(C02, 3, nr_service_pack_w);
    DBMS_SQL.COLUMN_VALUE(C02, 4, nr_pacote_w);
    begin
		begin

		-- variavel de log caso ocorra problemas
		nr_seq_ajuste_versao_w:= somente_numero(cd_versao_p||nr_release_w);

        if (length(nm_objeto_w) > 0) then
          CALL bkp_objeto_ajuste_versao(cd_versao_p, nr_release_w, nm_objeto_w, nm_usuario_p);
        end if;
		dt_ini_compile := clock_timestamp();
		-- compila os objetos
		CALL compile_ajuste_cloud(nm_usuario_p, nr_seq_ajuste_versao_w, 'C', cd_versao_p, nr_release_w);
		dt_fin_compile := clock_timestamp();

		-- a principio valida o codigo nao caiu do exception
		update	ajuste_versao_cliente
		set	ie_compila = 'S',
			dt_ini_aplicacao = dt_ini_compile,
			dt_fim_aplicacao = dt_fin_compile,
			nm_usuario_aplicacao = nm_usuario_p
		where	cd_versao = cd_versao_p
		and	nr_release = nr_release_w;
		commit;

		-- caiu no expception erro no codigo
		exception
		when others then
			update	ajuste_versao_cliente
			set	ie_compila = 'E'
			where	cd_versao = cd_versao_p
			and	nr_release = nr_release_w;
			commit;
            CALL recompile_objeto_release(cd_versao_p, nr_release_w,nm_usuario_p);
			CALL insert_log_cloud(cd_versao_p, get_Version_Tasy_Versao, nm_usuario_p, nm_fase_w||' - service pack '||nr_service_pack_w||' - pacote '||nr_pacote_w||', release '||nr_release_w, null, null, true,'C',nr_seq_ajuste_versao_w,nr_pacote_w,nr_release_w,nr_service_pack_w);
		end;
	end;

end loop;
CALL valida_objetos_sistema();
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
