CREATE OR REPLACE PROCEDURE aplicar_desc_nf_item ( nr_sequencia_p bigint, vl_desconto_p bigint, pr_desconto_p bigint, cd_estabelecimento_p bigint) AS $body$
DECLARE


vl_total_item_nf_w		double precision;
vl_tot_itens_nf_w		double precision;
qt_item_nf_w			double precision;
nr_item_nf_w			integer;
pr_desconto_w			double precision;
vl_desconto_w			double precision;
qt_itens_nf_w			integer;

c01 CURSOR FOR
SELECT	nr_item_nf,
	qt_item_nf,
	vl_total_item_nf
from	nota_fiscal_item
where	nr_sequencia = nr_sequencia_p
and	cd_estabelecimento = cd_estabelecimento_p;


BEGIN

select	sum(vl_total_item_nf)
into STRICT	vl_tot_itens_nf_w
from	nota_fiscal_item
where	nr_sequencia = nr_sequencia_p
and	cd_estabelecimento = cd_estabelecimento_p;

if (vl_desconto_p > 0) or (pr_desconto_p > 0) then
	open c01;
	loop
	fetch c01 into
		nr_item_nf_w,
		qt_item_nf_w,
		vl_total_item_nf_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		if (vl_desconto_p > 0) then
			pr_desconto_w :=  ((vl_desconto_p * 100)/vl_tot_itens_nf_w);
			vl_desconto_w := ((vl_total_item_nf_w * pr_desconto_w)/100);
		elsif (pr_desconto_p > 0) then
			vl_desconto_w := ((vl_total_item_nf_w * pr_desconto_p)/100);
			pr_desconto_w := pr_desconto_p;
		end if;

		update	nota_fiscal_item
		set	vl_desconto = vl_desconto_w,
			pr_desconto = pr_desconto_w,
			vl_liquido = (vl_total_item_nf_w - vl_desconto_w)
		where	nr_item_nf = nr_item_nf_w
		and	nr_sequencia = nr_sequencia_p
		and	cd_estabelecimento = cd_estabelecimento_p;
		end;
	end loop;
	close c01;
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
