SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE aprazar_item_prescr_interv ( ie_tipo_item_p wl_worklist.ie_tipo_item_cpoe%type, nr_prescricao_p prescr_medica.nr_prescricao%type, nr_seq_item_p prescr_material.nr_sequencia%type, dt_primeiro_horario_p timestamp, nr_seq_motivo_p bigint, ds_justificativa_p text, nm_usuario_p prescr_material.nm_usuario%type, nr_seq_assinatura_p bigint default null) AS $body$
DECLARE

					
dt_prim_horario_w	timestamp;
hr_prim_horario_w	timestamp;
nr_horas_validade_w	integer;
cd_material_w		integer;
cd_intervalo_w		varchar(7);
qt_dose_w		double precision;
qt_hora_intervalo_w	smallint;
qt_min_intervalo_w	integer;
nr_intervalo_w		double precision;
ds_horarios_w		varchar(255);
ds_horarios_ww		varchar(255);
ds_horarios_www		varchar(2000);
nr_seq_mat_cpoe_w	prescr_material.nr_seq_mat_cpoe%type;
ds_horarios_cpoe_w	cpoe_material.ds_horarios%type;
nr_seq_worklist_w	wl_worklist.nr_sequencia%type;


BEGIN
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_item_p IS NOT NULL AND nr_seq_item_p::text <> '') and (dt_primeiro_horario_p IS NOT NULL AND dt_primeiro_horario_p::text <> '') then
	
	if (ie_tipo_item_p in ('S','M','IA', 'IAH')) then
		select	a.dt_primeiro_horario,
			a.nr_horas_validade,
			b.cd_material,
			dt_primeiro_horario_p, --nvl(b.hr_prim_horario,a.dt_primeiro_horario),
			b.cd_intervalo,		
			b.qt_dose,
			b.qt_hora_intervalo,
			b.qt_min_intervalo
		into STRICT	dt_prim_horario_w,
			nr_horas_validade_w,
			cd_material_w,
			hr_prim_horario_w,
			cd_intervalo_w,
			qt_dose_w,
			qt_hora_intervalo_w,
			qt_min_intervalo_w
		from	prescr_medica a,
			prescr_material b
		where	a.nr_prescricao	= b.nr_prescricao
		and	a.nr_prescricao	= nr_prescricao_p
		and	b.nr_sequencia	= nr_seq_item_p;
		
		SELECT * FROM calcular_horario_prescricao(nr_prescricao_p, cd_intervalo_w, dt_prim_horario_w, hr_prim_horario_w, nr_horas_validade_w, cd_material_w, qt_hora_intervalo_w, qt_min_intervalo_w, nr_intervalo_w, ds_horarios_w, ds_horarios_ww, 'N', null) INTO STRICT nr_intervalo_w, ds_horarios_w, ds_horarios_ww;
		
		ds_horarios_www	:= ds_horarios_w || ds_horarios_ww;
		
		if (ie_tipo_item_p = 'M') then
			select  max(b.nr_seq_mat_cpoe),
                    max(a.ds_horarios)
				into STRICT 	nr_seq_mat_cpoe_w,
						ds_horarios_cpoe_w
				from cpoe_material a,
				prescr_material b
			where b.nr_seq_mat_cpoe	= a.nr_sequencia
			and b.nr_prescricao		= nr_prescricao_p
			and b.nr_sequencia		= nr_seq_item_p;
			
			if ((nr_seq_mat_cpoe_w IS NOT NULL AND nr_seq_mat_cpoe_w::text <> '') and coalesce(ds_horarios_cpoe_w::text, '') = '') then
				update cpoe_material
				set ds_horarios = ds_horarios_www
				where nr_sequencia = nr_seq_mat_cpoe_w;
			end if;
			
			Select max(nr_sequencia)
				into STRICT	nr_seq_worklist_w
			from	wl_worklist
			where nr_seq_item_cpoe = nr_seq_mat_cpoe_w
			and	ie_tipo_item_cpoe = ie_tipo_item_p
			and nr_seq_item = obter_se_worklist_lib_cat('NS');		

			CALL wl_alterar_fim_previsto(nr_seq_worklist_w, clock_timestamp() + interval '1 days', clock_timestamp(), nm_usuario_p, trim(both obter_desc_expressao(1079600)), null);

		end if;
		
		CALL gerar_aprazamento_interv(ie_tipo_item_p, nr_prescricao_p, nr_seq_item_p, ds_horarios_www, nr_seq_motivo_p, ds_justificativa_p, nm_usuario_p, nr_seq_assinatura_p);	
	end if;
	
end if;

if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE aprazar_item_prescr_interv ( ie_tipo_item_p wl_worklist.ie_tipo_item_cpoe%type, nr_prescricao_p prescr_medica.nr_prescricao%type, nr_seq_item_p prescr_material.nr_sequencia%type, dt_primeiro_horario_p timestamp, nr_seq_motivo_p bigint, ds_justificativa_p text, nm_usuario_p prescr_material.nm_usuario%type, nr_seq_assinatura_p bigint default null) FROM PUBLIC;

