CREATE OR REPLACE PROCEDURE aprovar_laudo_anatomia_pat ( nr_sequencia_p bigint, ie_tipo_aprovacao_p bigint, ie_forma_aprovacao_p text, qt_caracteres_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_liberado_w	varchar(1);


BEGIN
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin

	select	CASE WHEN coalesce(dt_liberacao::text, '') = '' THEN 'N'  ELSE 'S' END
	into STRICT	ie_liberado_w
	from	laudo_paciente
	where	nr_sequencia = nr_sequencia_p;

	if (ie_tipo_aprovacao_p = 0) then
		begin
		if (ie_liberado_w = 'N') and (ie_forma_aprovacao_p = 'A') then
			begin
			update	laudo_paciente
			set	dt_aprovacao 		= clock_timestamp(),
				nm_usuario_aprovacao 	= nm_usuario_p,
				dt_liberacao 		= clock_timestamp(),
				qt_caracteres		= qt_caracteres_p,
				nm_usuario		= nm_usuario_p
			where 	nr_sequencia 		= nr_sequencia_p;
			end;
		else
			begin
			update	laudo_paciente
			set	dt_aprovacao 		= clock_timestamp(),
				nm_usuario_aprovacao 	= nm_usuario_p,
				dt_liberacao 		 = NULL,
				qt_caracteres		= qt_caracteres_p,
				nm_usuario		= nm_usuario_p
			where 	nr_sequencia 		= nr_sequencia_p;
			end;
		end if;
		end;

	elsif (ie_tipo_aprovacao_p = 1) then
		begin
		if (ie_liberado_w = 'N') and (ie_forma_aprovacao_p = 'S') then
			begin
			update	laudo_paciente
			set	dt_seg_aprovacao	= clock_timestamp(),
				nm_usuario_seg_aprov	= nm_usuario_p,
				dt_liberacao		= clock_timestamp(),
				qt_caracteres		= qt_caracteres_p,
				nm_usuario		= nm_usuario_p
			where 	nr_sequencia		= nr_sequencia_p;
			end;
		else
			begin
			update	laudo_paciente
			set	dt_seg_aprovacao	= clock_timestamp(),
				nm_usuario_seg_aprov	= nm_usuario_p,
				dt_liberacao		 = NULL,
				qt_caracteres		= qt_caracteres_p,
				nm_usuario		= nm_usuario_p
			where 	nr_sequencia		= nr_sequencia_p;
			end;
		end if;
		end;
	end if;
	end;
end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
