SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE aprovar_material_autor_cir (nr_seq_autorizacao_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_material_w	bigint;

c01 CURSOR FOR
SELECT	nr_sequencia
from 	material_autor_cirurgia
where	nr_seq_autorizacao		= nr_seq_autorizacao_p
and	coalesce(ie_aprovacao,'N')	= 'N';


BEGIN

if (nr_seq_autorizacao_p IS NOT NULL AND nr_seq_autorizacao_p::text <> '') then
	open c01;
	loop
	fetch c01 into
		nr_seq_material_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */

		update	material_autor_cirurgia
		set	ie_aprovacao	= 'S',
			nm_usuario	= nm_usuario_p,
			dt_atualizacao	= clock_timestamp()
		where	nr_sequencia	= nr_seq_material_w;

	end loop;
	close c01;

	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
