CREATE OR REPLACE PROCEDURE aprova_montagem_agenda ( nr_seq_agenda_p bigint, ie_item_p text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


/*
ie_item_p
2 - Equipamento;3 - CME;4 - Kit;5 - Fax ;6 Aprovar e n√£o desaprovar todos os kits
*/
BEGIN

if (ie_item_p = '2') then
	update	agenda_monta_equipamento
	set	nm_usuario 		= nm_usuario_p,
		dt_aprovacao		= CASE WHEN dt_aprovacao = NULL THEN clock_timestamp()  ELSE null END
	where	nr_seq_agenda		= nr_seq_agenda_p;
elsif (ie_item_p = '3') then
	update	agenda_monta_cme
	set	nm_usuario 		= nm_usuario_p,
		dt_aprovacao		= CASE WHEN dt_aprovacao = NULL THEN clock_timestamp()  ELSE null END
	where	nr_seq_agenda		= nr_seq_agenda_p;
elsif (ie_item_p = '4') then
	update	agenda_pac_pedido
	set	nm_usuario 		= nm_usuario_p,
		dt_aprovacao		= CASE WHEN dt_aprovacao = NULL THEN clock_timestamp()  ELSE null END
	where	nr_seq_agenda		= nr_seq_agenda_p;
elsif (ie_item_p = '5') then
	update	agenda_paciente_inf_adic
	set	nm_usuario 		= nm_usuario_p,
		dt_aprovacao		= CASE WHEN dt_aprovacao = NULL THEN clock_timestamp()  ELSE null END
	where	nr_seq_agenda		= nr_seq_agenda_p;
elsif (ie_item_p = '6') then
	update	agenda_pac_pedido
	set	nm_usuario 		= nm_usuario_p,
		dt_aprovacao		= clock_timestamp()
	where	nr_seq_agenda		= nr_seq_agenda_p;
end if;

commit;

end;
$body$
