CREATE OR REPLACE PROCEDURE aprova_reprova_solic_mat ( nr_sequencia_p bigint, ie_opcao_p bigint, ds_justificativa_p text, nm_usuario_p text) AS $body$
BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (ie_opcao_p IS NOT NULL AND ie_opcao_p::text <> '') and (ds_justificativa_p IS NOT NULL AND ds_justificativa_p::text <> '') then
	update	material_padronizacao
	set	dt_reprovacao		= CASE WHEN ie_opcao_p=1 THEN dt_reprovacao  ELSE clock_timestamp() END ,
		dt_aprovacao 		= CASE WHEN ie_opcao_p=1 THEN clock_timestamp()  ELSE dt_aprovacao END ,
		ds_justificativa 	= ds_justificativa_p,
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nr_sequencia		= nr_sequencia_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
