SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE apurar_result_terceiro ( cd_estabelecimento_p bigint, cd_tabela_custo_p bigint, ie_atualizar_indic_p text, nr_seq_tabela_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_centro_controle_w		integer;
cd_empresa_w			bigint;
vl_mes_w				double precision;
vl_hospital_w			double precision;
vl_terceiro_w			double precision;
nr_sequencia_w			bigint;
ie_regra_w			varchar(3);
pr_hospital_w			double precision;
pr_terceiro_w			double precision;
cd_grupo_nat_gasto_w		integer;
cd_natureza_gasto_w		numeric(20);
nr_seq_result_terc_w		bigint;
nr_seq_gng_w			bigint;
nr_seq_ng_w			bigint;

c01 CURSOR FOR
SELECT	a.cd_centro_controle,
	b.nr_sequencia
from	cus_centro_terc b,
	centro_controle a
where	a.cd_estabelecimento = b.cd_estabelecimento
and	a.cd_centro_controle = b.cd_centro_controle
and	a.cd_estabelecimento = cd_estabelecimento_p
and	a.cd_centro_controle in (select x.cd_centro_controle
					from	centro_controle x,
						resultado_centro_controle y
					where	y.cd_centro_controle  = x.cd_centro_controle
					and 	x.ie_tipo_centro_controle = 2
					and 	x.ie_centro_resultado	= 'S'
					and	y.nr_seq_tabela		= nr_seq_tabela_p
					and	y.ie_classif_conta	= 900);

c02 CURSOR FOR
SELECT	sum(a.vl_mes) vl_mes
from	natureza_gasto n,
	centro_controle c,
	resultado_centro_controle a
where	a.cd_centro_controle	= c.cd_centro_controle
and	a.nr_seq_ng		= n.nr_sequencia
and	a.cd_estabelecimento	= coalesce(n.cd_estabelecimento, a.cd_estabelecimento)
and	a.cd_estabelecimento	= cd_estabelecimento_p
and	a.nr_seq_tabela		= nr_seq_tabela_p
and	a.cd_centro_controle	= cd_centro_controle_w
and	n.cd_empresa		= cd_empresa_w
group by	n.ds_natureza_gasto
order by	n.ds_natureza_gasto;

c03 CURSOR FOR
SELECT	ie_regra,
	pr_hospital,
	pr_terceiro,
	cd_grupo_nat_gasto,
	nr_seq_gng,
	cd_natureza_gasto,
	nr_seq_ng
from	cus_centro_terc_crit
where	nr_seq_centro_terc = nr_sequencia_w;




BEGIN

delete	FROM cus_result_terc
where	nr_seq_tabela	= nr_seq_tabela_p;

cd_empresa_w	:= obter_empresa_estab(cd_estabelecimento_p);

open c01;
loop
fetch c01 into
	cd_centro_controle_w,
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	open c02;
	loop
	fetch c02 into
		vl_mes_w;
	EXIT WHEN NOT FOUND; /* apply on c02 */

		open c03;
		loop
		fetch c03 into
			ie_regra_w,
			pr_hospital_w,
			pr_terceiro_w,
			cd_grupo_nat_gasto_w,
			nr_seq_gng_w,
			cd_natureza_gasto_w,
			nr_seq_ng_w;
		EXIT WHEN NOT FOUND; /* apply on c03 */
			if (ie_regra_w = 'R') then
				vl_hospital_w := dividir(vl_mes_w * pr_hospital_w, 100);
				vl_terceiro_w := vl_mes_w - vl_hospital_w;
			end if;

			select	nextval('cus_result_terc_seq')
			into STRICT	nr_seq_result_terc_w
			;

			insert into cus_result_terc(
				nr_sequencia,
				cd_estabelecimento,
				cd_tabela_custo,
				cd_centro_controle,
				ie_classif_conta,
				nr_seq_centro,
				vl_hospital,
				vl_terceiro,
				vl_total,
				cd_natureza_gasto,
				pr_rec_liq,
				dt_atualizacao,
				nm_usuario,
				nr_seq_ng,
				nr_seq_tabela)
			values (nr_seq_result_terc_w,
				cd_estabelecimento_p,
				cd_tabela_custo_p,
				cd_centro_controle_w,
				nr_seq_result_terc_w,
				nr_seq_result_terc_w,
				vl_hospital_w,
				vl_terceiro_w,
				vl_mes_w,
				cd_natureza_gasto_w,
				1,
				clock_timestamp(),
				nm_usuario_p,
				nr_seq_ng_w,
				nr_seq_tabela_p);
		end loop;
		close c03;
	end loop;
	close c02;
end loop;
close c01;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE apurar_result_terceiro ( cd_estabelecimento_p bigint, cd_tabela_custo_p bigint, ie_atualizar_indic_p text, nr_seq_tabela_p bigint, nm_usuario_p text) FROM PUBLIC;

