CREATE OR REPLACE PROCEDURE arm_update_proj_projeto ( NM_USUARIO_P text, NR_SEQ_ARM_PROJ_P bigint ) AS $body$
DECLARE

dt_initial_w			timestamp;
dt_final_w				timestamp;
ds_description_w		varchar(4000);
nr_seq_proj_project_w	bigint;


BEGIN
	select	dt_initial,
			dt_final,
			ds_description,
			nr_project_id
	into STRICT	dt_initial_w,
			dt_final_w,
			ds_description_w,
			nr_seq_proj_project_w
	from	arm_project
	where	nr_sequencia = NR_SEQ_ARM_PROJ_P;

	if (nr_seq_proj_project_w IS NOT NULL AND nr_seq_proj_project_w::text <> '') then
		update	proj_projeto
		set		dt_inicio_prev		= dt_initial_w,
				dt_fim_prev			= dt_final_w,
				ds_escopo			= ds_description_w,
				nm_usuario			= NM_USUARIO_P,
				dt_atualizacao		= clock_timestamp()
		where	nr_sequencia		= nr_seq_proj_project_w;
	end if;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
