CREATE OR REPLACE PROCEDURE arquivo_mercadoria_servico (cd_estabelecimento_p bigint, dt_inicio_p timestamp, dt_fim_p timestamp, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w			varchar(10);
nr_item_nf_w			varchar(10);
dt_atualizacao_w		varchar(8);
cd_item_nf_w			varchar(20);
ds_item_nf_w			varchar(45);
cd_estabelecimento_w		varchar(10);
contador_w			bigint;
ds_arquivo_ww			varchar(4000);


c01 CURSOR FOR
SELECT	a.nr_sequencia							nr_sequencia,
	b.nr_item_nf							nr_item_nf,
	lpad(coalesce(to_char(a.dt_atualizacao,'ddmmyyyy'),' '),8,' ')						dt_atualizacao,
	lpad(coalesce(to_char(CASE WHEN coalesce(b.cd_material::text, '') = '' THEN b.cd_procedimento  ELSE b.cd_material END ),' '),20,' ')	cd_item_nf,
	rpad(coalesce(CASE WHEN coalesce(b.cd_material::text, '') = '' THEN substr(obter_descricao_procedimento(b.cd_procedimento, b.ie_origem_proced),1,240)  ELSE substr(obter_desc_material(b.cd_material),1,100) END ,' '),45,' ') ds_item_nf,
	a.cd_estabelecimento						cd_estabelecimento
from	nota_fiscal a,
	nota_fiscal_item b
where	b.nr_sequencia = a.nr_sequencia
and	a.cd_estabelecimento = cd_estabelecimento_p
and	a.dt_emissao between to_date(to_char(dt_inicio_p,'dd/mm/yyyy'),'dd/mm/yyyy') and fim_dia(to_date(to_char(dt_fim_p,'dd/mm/yyyy'),'dd/mm/yyyy'))
order by	a.nr_sequencia, b.nr_item_nf;


BEGIN

open c01;
loop
fetch c01 into

nr_sequencia_w,
nr_item_nf_w,
dt_atualizacao_w,
cd_item_nf_w,
ds_item_nf_w,
cd_estabelecimento_w;

EXIT WHEN NOT FOUND; /* apply on c01 */

	begin

	contador_w := contador_w + 1;

	ds_arquivo_ww	:=	dt_atualizacao_w	||
				cd_item_nf_w		||
				ds_item_nf_w;

insert into w_inss_direp_arquivo( nr_sequencia,
				cd_estabelecimento,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				ds_arquivo_w)
		values (nextval('w_inss_direp_arquivo_seq'),
				cd_estabelecimento_w,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				ds_arquivo_ww);

	if (mod(contador_w,100) = 0) then
		commit;
	end if;

	end;

end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
