CREATE OR REPLACE PROCEDURE arquivo_natureza_operacao (cd_estabelecimento_p bigint, dt_inicio_p timestamp, dt_fim_p timestamp, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w			varchar(10);
dt_atualizacao_w		varchar(8);
cd_natureza_operacao_w		varchar(6);
ds_natureza_operacao_w		varchar(45);
cd_estabelecimento_w		varchar(10);
contador_w			bigint;
ds_arquivo_ww			varchar(4000);


c01 CURSOR FOR
SELECT	distinct
	n.nr_sequencia							nr_sequencia,
	lpad(coalesce(to_char(o.dt_atualizacao,'ddmmyyyy'),' '),8,' ')	dt_atualizacao,
	lpad(coalesce(o.cd_cfop,' '),6,' ') 					cd_natureza_operacao,
	rpad(coalesce(ds_natureza_operacao,' '),45,' ')			ds_natureza_operacao
from	nota_fiscal n,
	natureza_operacao o
where	o.cd_natureza_operacao = n.cd_natureza_operacao
and	n.cd_estabelecimento = cd_estabelecimento_p
and	n.dt_emissao between to_date(to_char(dt_inicio_p,'dd/mm/yyyy'),'dd/mm/yyyy') and fim_dia(to_date(to_char(dt_fim_p,'dd/mm/yyyy'),'dd/mm/yyyy'))
order by n.nr_sequencia;


BEGIN

open c01;
loop
fetch c01 into

nr_sequencia_w,
dt_atualizacao_w,
cd_natureza_operacao_w,
ds_natureza_operacao_w;

EXIT WHEN NOT FOUND; /* apply on c01 */

	begin

	contador_w := contador_w + 1;

	ds_arquivo_ww	:=	dt_atualizacao_w	||
				cd_natureza_operacao_w	||
				ds_natureza_operacao_w;

insert into w_inss_direp_arquivo( nr_sequencia,
				cd_estabelecimento,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				ds_arquivo_w)
		values (nextval('w_inss_direp_arquivo_seq'),
				cd_estabelecimento_p,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				ds_arquivo_ww);

	if (mod(contador_w,100) = 0) then
		commit;
	end if;

	end;

end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
