CREATE OR REPLACE PROCEDURE arredondamento (vl_informado_p INOUT bigint, nr_decimais_p bigint, ie_tipo_rounded_p text) AS $body$
BEGIN
if  ie_tipo_rounded_p = 'D' then
    vl_informado_p := TRUNC(vl_informado_p , NR_DECIMAIS_P);
else
    vl_informado_p := ROUND(vl_informado_p , NR_DECIMAIS_P);
END IF;
END;
