CREATE OR REPLACE PROCEDURE assinar_laudo_paciente ( cd_pessoa_fisica_p text, nr_seq_interno_p bigint, ie_commit_p text ) AS $body$
BEGIN
begin
update 	sus_laudo_paciente
set 	cd_medico_requisitante  = cd_pessoa_fisica_p,
	dt_assinatura_laudo     = clock_timestamp()
where 	nr_seq_interno        	= nr_seq_interno_p;
exception
when others then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(996133);
	--A pessoa informada não é medico(a)!
end;

if (coalesce(ie_commit_p,'N') = 'S') then
	commit;
end if;

end;
$body$
