CREATE OR REPLACE PROCEDURE assinar_sismama_atendimento ( cd_pessoa_fisica_p text, nr_sequencia_p bigint, ie_commit_p text ) AS $body$
BEGIN
begin
update 	sismama_atendimento
set 	cd_profissional  = cd_pessoa_fisica_p,
	dt_assinatura_laudo     = clock_timestamp()
where 	nr_sequencia        	= nr_sequencia_p;
exception
when others then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(996133);
	--A pessoa informada não é medico(a)!
end;

if (coalesce(ie_commit_p,'N') = 'S') then
	commit;
end if;

end;
$body$
