CREATE OR REPLACE PROCEDURE assume_atend_hospitalidade ( cd_pessoa_fisica_p text, nr_atendimento_p bigint) AS $body$
DECLARE


nr_sequencia_w		bigint;


BEGIN

begin
select	max(nr_sequencia)
into STRICT	nr_sequencia_w
from	atend_profissional
where	cd_pessoa_fisica = cd_pessoa_fisica_p
and	nr_atendimento		 = nr_atendimento_p
and ((ie_profissional	 = 'H') or (IS_COPY_BASE_LOCALE('es_MX') = 'S' and ie_profissional = '12'));
exception
	when others then
		nr_sequencia_w	:= 0;
end;

if	((coalesce(nr_sequencia_w::text, '') = '') or (nr_sequencia_w = 0)) and (coalesce(nr_atendimento_p,0) > 0) then

	insert into atend_profissional(
		nr_sequencia,
		nm_usuario,
		dt_atualizacao,
		nm_usuario_nrec,
		dt_atualizacao_nrec,
		nr_atendimento,
		cd_pessoa_fisica,
		ie_profissional,
		dt_inicio_vigencia
	) values (
		nextval('atend_profissional_seq'),
		obter_usuario_pf(cd_pessoa_fisica_p),
		clock_timestamp(),
		obter_usuario_pf(cd_pessoa_fisica_p),
		clock_timestamp(),
		nr_atendimento_p,
		cd_pessoa_fisica_p,
		'H',
		clock_timestamp()
	);

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
