CREATE OR REPLACE PROCEDURE assumir_protocolo_onco ( nr_seq_paciente_p bigint, cd_medico_p text, nm_usuario_p text) AS $body$
BEGIN

update	paciente_setor
set	cd_medico_resp	= cd_medico_p,
	nm_usuario	= nm_usuario_p
where	nr_seq_paciente	= nr_seq_paciente_p;

insert into paciente_setor_medico(
	nr_sequencia,
	dt_atualizacao,
	nm_usuario,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	nr_seq_paciente,
	cd_medico,
	dt_inicio)
SELECT	nextval('paciente_setor_medico_seq'),
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario_p,
	nr_seq_paciente_p,
	cd_medico_p,
	clock_timestamp()
;

commit;

END;
$body$
