CREATE OR REPLACE PROCEDURE atender_ordem_medica ( nr_seq_ordem_p bigint, cd_pessoa_p text, nm_usuario_p text) AS $body$
DECLARE


dt_realizacao_w		timestamp;
nr_prescricao_w		bigint;
nr_atendimento_w	bigint;
nr_seq_alteracao_w	bigint;
nr_seq_ordem_w		bigint;


BEGIN

select	max(a.dt_fim_horario),
	max(b.nr_prescricao),
	max(b.nr_atendimento),
	max(b.nr_sequencia)
into STRICT	dt_realizacao_w,
	nr_prescricao_w,
	nr_atendimento_w,
	nr_seq_ordem_w
from	prescr_medica_ordem b,
	prescr_ordem_hor a
where	a.nr_sequencia	= nr_seq_ordem_p
and	a.nr_seq_ordem	= b.nr_sequencia
and	a.ie_horario_especial = 'N';

if (coalesce(dt_realizacao_w::text, '') = '') then
	update	prescr_medica_ordem
	set	cd_pessoa_real	= cd_pessoa_p,
		dt_realizacao	= clock_timestamp(),
		nm_usuario	= nm_usuario_p
	where	nr_sequencia	= nr_seq_ordem_w;

	update	prescr_ordem_hor
	set	dt_fim_horario	= clock_timestamp(),
		nm_usuario_fim	= nm_usuario_p
	where	nr_sequencia	= nr_seq_ordem_p
	and		ie_horario_especial = 'N';

	select	nextval('prescr_mat_alteracao_seq')
	into STRICT	nr_seq_alteracao_w
	;

	insert into prescr_mat_alteracao(nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_prescricao,
		nr_seq_horario_ordem,
		dt_alteracao,
		cd_pessoa_fisica,
		ie_alteracao,
		nr_seq_qualidade,
		ds_justificativa,
		ds_observacao,
		ie_tipo_item,
		dt_horario,
		nr_atendimento,
		cd_item,
		qt_dose_adm,
		qt_dose_original)
	values (
		nr_seq_alteracao_w,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_prescricao_w,
		nr_seq_ordem_p,
		clock_timestamp(),
		cd_pessoa_p,
		3,
		null,
		null,
		null,
		'B',
		clock_timestamp(),
		nr_atendimento_w,
		nr_seq_ordem_p,
		0,
		0);

	commit;

else
	CALL Wheb_mensagem_pck.exibir_mensagem_abort(261378);
	--'Esta ordem j√° foi atendida!
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
