CREATE OR REPLACE PROCEDURE atender_solic_exame ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_emprestimo_w		bigint;
nr_sequencia_w		bigint;
nr_prontuario_w		bigint;
cd_estabelecimento_w	smallint;
cd_setor_atendimento_w	integer;
cd_agenda_w		bigint;


BEGIN

select	cd_estabelecimento,
	nr_prontuario,
	cd_setor_atendimento,
	cd_agenda
into STRICT	cd_estabelecimento_w,
	nr_prontuario_w,
	cd_setor_atendimento_w,
	cd_agenda_w
from	exame_solicitacao
where	nr_sequencia	= nr_sequencia_p;

select	count(*)
into STRICT	qt_emprestimo_w
from	exame_emprestimo
where	nr_prontuario	= nr_prontuario_w
and	coalesce(dt_devolucao::text, '') = '';

if (qt_emprestimo_w <> 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(800286);
end if;

select	nextval('exame_emprestimo_seq')
into STRICT	nr_sequencia_w
;

insert into exame_emprestimo(
	nr_sequencia,
	cd_estabelecimento,
	nr_prontuario,
	dt_atualizacao,
	nm_usuario,
	cd_setor_atendimento,
	dt_emprestimo,
	nm_usuario_empr,
	ie_operacao,
	cd_agenda,
	nr_seq_solic)
Values (
	nr_sequencia_w,
	cd_estabelecimento_w,
	nr_prontuario_w,
	clock_timestamp(),
	nm_usuario_p,
	cd_setor_atendimento_w,
	clock_timestamp(),
	nm_usuario_p,
	'E',
	cd_agenda_w,
	nr_sequencia_p);

update	exame_solicitacao
set	ie_status_solic = 'T',
	nm_usuario	= nm_usuario_p,
	dt_atualizacao	= clock_timestamp()
where	nr_sequencia	= nr_sequencia_p;

commit;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
