CREATE OR REPLACE PROCEDURE atende_via_fila_espera ( nr_atendimento_p bigint, cd_pessoa_fisica_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_pac_espera_w	bigint;


BEGIN

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then

	select	coalesce(max(nr_sequencia),0)
	into STRICT	nr_seq_pac_espera_w
	from	paciente_espera
	where	cd_pessoa_fisica = cd_pessoa_fisica_p
	and	coalesce(nr_atendimento::text, '') = '';

	if (nr_seq_pac_espera_w <> 0) then

		update	paciente_espera
		set	nr_atendimento 	= nr_atendimento_p,
			dt_atualizacao 	= clock_timestamp(),
			nm_usuario	= nm_usuario_p
		where	nr_sequencia	= nr_seq_pac_espera_w
		and	coalesce(nr_atendimento::text, '') = '';

		commit;

	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
